// Banner.js

import React from 'react';

const Banner = ({ title }) => {
  return (
    <div style={bannerStyles}>
      <h1>{title}</h1>
    </div>
  );
};

const bannerStyles = {
  backgroundColor: '#3498db',
  color: '#fff',
  padding: '20px',
  textAlign: 'center',
};

export default Banner;
